import SwiftUI

final class DescriptionModel: ObservableObject {
    @Published private(set) var description: String
    
    init() {
        debugPrint(#fileID, #function)
        self.description = ""
    }
    
    init(description: String) {
        debugPrint(#fileID, #function)
        self.description = description
    }
    
    func updateDescription(_ description: String) {
        debugPrint(#fileID, #function)
        self.description = description
    }
}

struct DescriptionView: View {
    @ObservedObject private(set) var model: DescriptionModel
    private let onURLTapAction: ((URL) -> Void)?
    private let font: UIFont
    
    init(model: DescriptionModel, font: UIFont = .withStyle(.body), onURLTapAction: ((URL) -> Void)? = nil) {
        self.model = model
        self.font = font
        self.onURLTapAction = onURLTapAction
    }
    
    var body: some View {
        GeometryReader { geometry in
            ScrollView(.vertical) {
                VStack(alignment: .center, spacing: 32.0) {
                    Image("logoSmallHorizontal")
                        .resizable()
                        .scaledToFit()
                        .frame(height: 70.0, alignment: .center)
                    HTMLContent(text: model.description,
                                font: font,
                                textAlignment: .justified,
                                geometry: geometry,
                                offset: 32.0,
                                onURLTapAction: onURLTapAction)
                    .padding([.leading, .trailing], 16.0)
                }
                .padding(.bottom, 16.0)
            }
        }
    }
}

